const aiMessage = items[1].json.message?.content ?? "";

let parsedAI = null;

// Try direct JSON parse first
try {
  parsedAI = JSON.parse(aiMessage);
} catch (err) {
  // If direct parse fails, try to extract from backtick format
  const jsonMatch = aiMessage.match(/```json([\s\S]*?)```/);

  if (jsonMatch) {
    try {
      parsedAI = JSON.parse(jsonMatch[1].trim());
    } catch (innerErr) {
      throw new Error("Backtick-wrapped block exists but is not valid JSON: " + innerErr.message);
    }
  } else {
    throw new Error("OpenAI response is not valid JSON and not wrapped in ```json``` block.\n\nResponse Content:\n" + aiMessage);
  }
}

// Add Drive metadata
