parsedAI.file_name = driveData.file_name;
parsedAI.mime_type = driveData.mime_type;
parsedAI.drive_file_id = driveData.id;

// Include email from Drive data
parsedAI.email = driveData.email;

return [
  {
    json: parsedAI
  }
