// Clean wrapping ```json and ``` tags
let cleaned = rawOutput
  .trim()
  .replace(/^```json[\r\n]?/, '') // remove ```json\n or ```json
  .replace(/```$/, '')            // remove trailing ```
  .replace(/\\n/g, '\n');         // convert escaped \n to actual newlines

let parsed;

try {
  parsed = JSON.parse(cleaned);
} catch (error) {
