// Input is an array with an "output" key that contains a JSON string
const input = items[0].json.output;

// Parse the string into an object
const parsed = JSON.parse(input);

// Return the parsed object as separate JSON
return [
  {
    json: {
      location: parsed.location,
      business: parsed.business,
      job_title: parsed.job_title
    }
  }
];

// Get the input JSON
const input = items[0].json;

// Convert + to %20 for URL encoding
const jobTitle = input.job_title.replace(/\+/g, '%20');
const location = input.location.replace(/\+/g, '%20');
const business = input.business.replace(/\+/g, '%20');

// Construct the final URL
const finalURL = `https://app.apollo.io/#/people?sortByField=recommendations_score&sortAscending=false&page=1&personTitles[]=${jobTitle}&personLocations[]=${location}&qOrganizationKeywordTags[]=${business}&includedOrganizationKeywordFields[]=tags&includedOrganizationKeywordFields[]=name`;

// Return the final URL
return [
  {
    json: {
      finalURL
    }
  }
];
