// N8N Code Node to combine multiple fields into a single field with new lines

// This code expects the input item to have the following fields:
// - Title
// - Introduction
// - ToC (Table of Contents)
// - Chapter 1
// - Topic 1 Content
// - Chapter 2
// - Topic 2 Content
// - Chapter 3
// - Topic 3 Content
// - Chapter 4
// - Topic 4 Content
// - Chapter 5
// - Topic 5 Content
// - Sources

// Access the incoming data
const item = items[0];

// Create a new field called "CombinedContent" that joins all fields with newlines
const combinedContent = [
  item.json.Title || '',
  item.json.Introduction || '',
  item.json.ToC || '',
  item.json['Chapter 1'] || '',
  item.json['Topic 1 Content'] || '',
  item.json['Chapter 2'] || '',
  item.json['Topic 2 Content'] || '',
  item.json['Chapter 3'] || '',
  item.json['Topic 3 Content'] || '',
  item.json['Chapter 4'] || '',
  item.json['Topic 4 Content'] || '',
  item.json['Chapter 5'] || '',
  item.json['Topic 5 Content'] || '',
  item.json.Sources || ''
].join('\n\n');

// Add the combined content to the item
item.json.CombinedContent = combinedContent;

// Return the modified item
return [item];

